import React, { useState } from 'react'
import { Link } from 'react-router-dom'

export const SignUp = () => {
    const [account, setAccount] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');

    const [error, setError] = useState('');
    const addAccount = (e) => {
        e.preventDefault();
        console.log(account, password, firstName, lastName)
    }
    const checkConfirmPassword = () => {
        if (confirmPassword != password) {
            setError('Passwords do not match');
        } else {
            setError('Valid Password');
        }
    }
    return (
        <div className="container">
            <h2 className="mt-3">SIGN UP</h2>
            <hr />
            <form
                autoComplete="off"
                className="row form-group"
                onSubmit={addAccount}
            >
                <label htmlFor="account" className="mt-2">Email or mobile phone number</label>
                <input
                    type="text"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setAccount(e.target.value)}
                    value={account}
                />
                <label htmlFor="password" className="mt-2">Password</label>
                <input
                    type="password"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                />
                <label htmlFor="confirm-password" className="mt-2">Confirm Password</label>
                <input
                    type="password"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    onBeforeInput={checkConfirmPassword}
                    value={confirmPassword}
                />
                {error && <span className="mt-2">{error}</span>}

                <label htmlFor="firstname" className="mt-2">First Name</label>
                <input
                    type="text"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setFirstName(e.target.value)}
                    value={firstName}
                />
                <label htmlFor="lastname" className="mt-2">Last Name</label>
                <input
                    type="text"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setLastName(e.target.value)}
                    value={lastName}
                />
                <button className="btn btn-success btn-md w-100 mt-4">SIGN UP</button>
            </form>
        </div>
    )
}
