import React, { useState } from 'react'
import '../css/SignUp.css'
export const SignUp = () => {
    const [account, setAccount] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');

    const [error, setError] = useState('');
    const addAccount = (e) => {
        e.preventDefault();
        console.log(account, password, firstName, lastName)
        if (confirmPassword != password) {
            setError('Passwords do not match');
        } else {
            setError('');
        }
    }
    return (
        <div className="container">
            <h2 className="mt-3">SIGN UP</h2>
            <hr />
            <form
                autoComplete="off"
                className="form-group"
                onSubmit={addAccount}
            >
                <label htmlFor="account" className="mt-2">Email or mobile phone number</label>
                <input
                    type="text"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setAccount(e.target.value)}
                    value={account}
                />
                <label htmlFor="password" className="mt-2">Password</label>
                <input
                    type="password"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                />
                <label htmlFor="confirm-password" className="mt-2">Confirm Password</label>
                <input
                    type="password"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    value={confirmPassword}
                />
                <div className='d-flex align-items-end flex-column'>
                    {error && <span className="mt-2 error">{error}</span>}
                </div>
                <label htmlFor="firstname" className="mt-2">First Name</label>
                <input
                    type="text"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setFirstName(e.target.value)}
                    value={firstName}
                />
                <label htmlFor="lastname " className="mt-2">Last Name</label>
                <input
                    type="text"
                    className="form-control mt-1"
                    required
                    onChange={(e) => setLastName(e.target.value)}
                    value={lastName}
                />
                <button className="btn btn-success btn-md w-100 mt-4">SIGN UP</button>
            </form>
        </div>
    )
}
