import React from 'react'
import Navbar from './Navbar'
import axios from "axios";    
import { useState, useEffect } from "react"
import { useParams } from "react-router-dom";
import '../css/ProductDetail.css'

export const ProductDetail = (props) => {
  const params = useParams();

  const [posts, setPosts] = useState([])

  useEffect(() => {
    axios.get(`http://localhost:3000/${params.id}`)
      .then(res => {
        console.log(res)
        setPosts(res.data)
      })
      .catch(err => {
        console.log(err)
      })
  }, [params.id])
  return (
    <div className="wrapper">
      <Navbar />
      <div className="container">
        <div className="details" >
          <div className="big-img">
            <img src="./images/1.png" alt="abc" />
          </div>
          <div className="box">
            <div className="row">
              <h2 className="name">Iphone 13 Promax</h2>
              <span className="price">$999</span>
            </div>
            <p className="description">Hands-free, Hads-on Mushc ExperienceHands-free, Hads-on Mushc Experience Hands-free, Hads-on Mushc ExperienceHands-free, Hads-on Mushc ExperienceHands-free, Hads-on Mushc ExperienceHands-free, Hads-on Mushc Experience</p>
            <button className="button">Add to cart</button>
          </div>
        </div>
      </div>
    </div>
  )
}
