import React, { useState } from 'react'
import Navbar from './Navbar';
import '../css/Cart.css';
import smartphone_card from '../data/smartphone_data.js'

const ProductCartItem = (product) => {
    console.log(product);
    return (
        <>
            <div className="cart_box">
                <div className="left-side">
                    <div className="cart_img">
                        <img src={product.thumb} alt="" />
                    </div>
                    <div className="wrapper-item">
                        <div className="name">{product.product_name}</div>
                    </div>
                    <div className="wrapper-item">
                        <div className="price">{product.price}</div>
                    </div>
                </div>
                <div className="right-side">
                    <div className="btn-group">
                        <button className="button">+</button>
                        <div className="wrapper-item amount">{product.amount}</div>
                        <button className="button">-</button>
                    </div>
                    <div className="last-price">{product.amount * product.price}$</div>
                    <button className="button">remove</button>
                </div>
            </div>
        </>
    )
}
const Cart = () => {
    return (
        <div className="container-cart">
            <Navbar />
            <ProductCartItem product={smartphone_card[0]}/>
            <div className="total-price">
                <div>Total: $199999</div>
                <button>payment</button>
                </div>
        </div>
    );
};

export default Cart;
