import React, { useState } from 'react'
import Navbar from './Navbar';
import '../css/Cart.css';
import smartphone_card from '../data/smartphone_data.js'

const ProductCartItem = (product, parentCallback) => {
    const [item, setItem] = useState(product.product);
    if (item.amount != null) {
        parentCallback(item.amount);

    }
    const addAmount = () => {
        let amount = item.amount + 1;
        setItem({
            ...item,
            amount: amount
        })
    }
    const reduceAmount = () => {
        if (item.amount > 0) {
            let amount = item.amount - 1;
            setItem({
                ...item,
                amount: amount
            })
        }

    }

    return (
        <>
            <div className="cart_box">
                <div className="left-side">
                    <div className="cart_img">
                        <img src={item.thumb} alt="" />
                    </div>
                    <div className="wrapper-item">
                        <div className="name">{item.product_name}</div>
                    </div>
                    <div className="wrapper-item">
                        <div className="price">${item.price}</div>
                    </div>
                </div>
                <div className="right-side">
                    <div className="btn-group">
                        <button className="button" onClick={() => reduceAmount()}>-</button>
                        <div className="wrapper-item amount">{item.amount} {console.log(item.amount)}
                        </div>
                        <button className="button" onClick={() => addAmount()}>+</button>
                    </div>
                    <div className="last-price">{item.amount * item.price}$</div>
                    <button className="button">remove</button>
                </div>
            </div>
        </>
    )
}
const Cart = () => {
    const [totalPrice, setTotalPrice] = useState(0);

    const handleCallback = ({ childData }) => {
        setTotalPrice(totalPrice + childData)
    }
    const productCart = smartphone_card.map((item) =>
        <ProductCartItem product={item} parentCallback={handleCallback} />
    );
    return (
        <div className="container-cart">
            <Navbar />
            {productCart}
            <div className="total-price">
                <div>Total: ${totalPrice}</div>
                <button>payment</button>
            </div>
        </div>
    );
};

export default Cart;
